% Fine-tune Pre-Trained Model on VBD Dataset
% model = "models/denoiseNetFullyConnected.mat";
% cleanDir = 'data/train/clean_trainset_wav';
% noisyDir = 'data/train/noisy_trainset_wav';
% outputPath = 'models/denoiseNet_FineTuned_VBD.mat';
% 
% train(model, cleanDir, noisyDir, outputPath);
%%
% Add noise to clean custom samples 
% cleanInputDir = "data/test/gabrielSamples/clean";
% noisyOutputDir = "data/test/gabrielSamples/output_wav"

% generateNoisyDir(cleanInputDir, noisyOutputDir) ==> modify so it takes input/output directories
%%
% Denoise noised custom samples
model = "models/denoiseNet_FineTuned_VBD.mat" %[output:6065f412]
noisyInputDir = "data/test/gabrielSamples/noisy";
denoisedOutputDir = "data/test/gabrielSamples/output_wav";
cleanOriginalDir = "data/test/gabrielSamples/clean";

denoisedAudioArray = denoiseSpeechDir(model, noisyInputDir, denoisedOutputDir);

% Find the average correlation between the original clean audio and the denoised audio
[~, avgCorr] = calculateCorrelationDir(denoisedAudioArray, cleanOriginalDir, 10, 10) %[output:508b8775] %[output:1526bd0e]
disp(avgCorr) %[output:22b00256]
%%
% Denoise VBD Test Set
model = "models/denoiseNet_FineTuned_VBD.mat"; %[output:302146a3]
noisyInputDir = "data/test/datasetSamples/noisy_testset_wav";
denoisedOutputDir = "data/test/datasetSamples/output_wav";
cleanOriginalDir = "data/test/datasetSamples/clean_testset_wav";

denoisedAudioArray = denoiseSpeechDir(model, noisyInputDir, denoisedOutputDir); %[output:153c0c00]

% Find the average correlation between the original clean audio and the denoised audio
[~, avgCorr] = calculateCorrelationDir(denoisedAudioArray, cleanOriginalDir)
disp(avgCorr)


